// store the 1000 digits in a variable 
// convert the variable into an array

// for loop- loop through the entire array
//   for each value, we have a sum that adds the next 13 indexes. There might have to be an if condition for the ending numbers

const hugeNumber = ''

const hugeNumberArray = [...hugeNumber]
const finalArray = hugeNumberArray.map(num => +num)

const slicedArrays = []

for (let i = 0; i < finalArray.length; i++) {
  const slice = finalArray.slice(i, i + 13)
  slicedArrays.push(slice)
}

// let total = slicedArrays[0].reduce((a, b) => a * b)
// let total = 0
let totals = slicedArrays.map(arr => arr.reduce((a,b) => a * b))
console.log(Math.max(...totals)) // 4: 5832 // 13: 23514624000
const solution = slicedArrays.findIndex(arr => arr.reduce((a,b) => a * b) === 23514624000)
const highestProductArray = slicedArrays[solution]
console.log(highestProductArray)

